import React, { useEffect, useState } from 'react'
import { Descriptions } from 'antd'
import {Collapse, Divider, Row, Col} from "antd"
const { Panel } = Collapse;
const  [purches, setPurches] = useState([])
const Purches = ({ purches, setPurch}) => {
    useEffect(() => {
    const getPurch = async () => {
    const requestOptions = {
    method: "GET"
    }
    return await fetch("/api/Client", requestOptions)
    
    .then(response => response.json())
    .then(
    (data) => {
    console.log('Data:', data)
    setPurch(data)
    },
    (error) => {
    console.log(error)
    }
    )
    }
    getPurch()
    }, [setPurch])
 
    return (
        <React.Fragment>
         <label style={{fontFamily:"Cinzel",textAlign: 'center',color:'white', fontWeight: 'lighter', fontSize: '20px', marginTop: '10px', color: 'var(--blue)', display: 'block' }}>Clients: </label>
         
        <div style={{ flexWrap: 'wrap' }}>
        {purches.map(({ id, fullName, driverLicense, pasport, date, totalAmount, accessName, modelName}) => (   
        <div className="Purchases" key={id} id={id}>
                  <Descriptions title="Purchases" layout="vertical" >
                 
                    <Descriptions.Item 
                    label="ФИО: "> {fullName}
                    </Descriptions.Item>

                    <Descriptions.Item 
                    label="Водительское удостоверение: "> {driverLicense}
                    </Descriptions.Item>

                    <Descriptions.Item 
                    label="Паспорт: "> {pasport}
                    </Descriptions.Item>

                    <Descriptions.Item 
                    label="Дата покупки: "> {date}
                    </Descriptions.Item>

                    <Descriptions.Item 
                    label="Конечная стоимость: "> {totalAmount}
                    </Descriptions.Item>

                    <Descriptions.Item 
                    label="Аксессуар: "> {accessName}
                    </Descriptions.Item>

                    <Descriptions.Item 
                    label="Комплектация: "> {modelName}
                    </Descriptions.Item>

                    
                    </Descriptions>
               
                
        {/* <img src={require("../../Images/" +"acc"+ id + ".png")} style={{ width: '80%', height: '200px' }} />
        <div style={{ fontFamily:"Cinzel", marginLeft:'130px', fontWeight: 'lighter', fontSize: '20px', marginTop: '10px', color: 'var(--blue)' }}>{name}</div> */}
        </div>
        ))}
        </div>
        

       
        </React.Fragment>
        )
        }
        export default Purches
